import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createGlobalStyle } from 'styled-components';
import aiTheme from './styles/theme'; // Import the new theme file
import Navigation from './components/Navigation';
import Chatbot from './components/Chatbot'; // Corrected import name
import IrrigationDashboard from './components/IrrigationDashboard';
import Iot from './components/Iot';
import './App.css';

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    font-family: ${({ theme }) => theme.fonts.primary};
    background-color: ${({ theme }) => theme.colors.background};
    color: ${({ theme }) => theme.colors.text};
  }

  #root {
    display: flex;
    min-height: 100vh;
  }
`;

const App = () => {
  return (
    <ThemeProvider theme={aiTheme}>
      <GlobalStyle />
      <Router>
        <div className="app-container">
          <Navigation />
          <main className="main-content">
            <Routes>
              <Route path="/" element={<Chatbot />} /> {/* Corrected component name */}
              <Route path="/dashboard" element={<IrrigationDashboard />} /> {/* Corrected path and component */}
              <Route path="/iot" element={<Iot />} /> {/* Corrected path */}
              <Route path="/settings" element={<div>Configurações Placeholder</div>} /> {/* Added settings route */}
            </Routes>
          </main>
        </div>
      </Router>
    </ThemeProvider>
  );
};

export default App;
