import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import useSmartAPI from '../hooks/useSmartAPI';
import SmartChart from './SmartChart';
import aiTheme from '../styles/theme'; // Import theme

const aiModel = {
  createDashboard: async ({ data, style, priority, responsiveness }) => {
    // Mock AI dashboard creation
    console.log('AI creating dashboard');
    return {
      grid: '1fr 1fr',
      widgets: [
        {
          id: 'sensor-data',
          title: 'Dados do Sensor', // Updated title
          dataSource: 'sensors',
          visualization: 'table',
          aiStyling: {
            background: aiTheme.colors.background, // Use theme color
            borderRadius: aiTheme.borderRadius, // Use theme borderRadius
            padding: aiTheme.spacing.medium, // Use theme spacing
            shadow: aiTheme.boxShadow, // Use theme boxShadow
          },
        },
        {
          id: 'irrigation-data',
          title: 'Dados de Irrigação', // Updated title
          dataSource: 'irrigation',
          visualization: 'chart',
          aiStyling: {
            background: aiTheme.colors.background, // Use theme color
            borderRadius: aiTheme.borderRadius, // Use theme borderRadius
            padding: aiTheme.spacing.medium, // Use theme spacing
            shadow: aiTheme.boxShadow, // Use theme boxShadow
          },
        },
      ],
    };
  },
};

const IrrigationDashboard = () => { // Renamed component
  const { data: sensorData } = useSmartAPI('sensors');
  const { data: irrigationData } = useSmartAPI('irrigation');
  const [dashboardLayout, setDashboardLayout] = useState(null);

  useEffect(() => {
    const generateDashboard = async () => {
      const layout = await aiModel.createDashboard({
        data: { sensors: sensorData, irrigation: irrigationData },
        style: 'modern_agricultural',
        priority: 'visual_impact',
        responsiveness: 'mobile_first',
      });

      setDashboardLayout(layout);
    };

    if (sensorData && irrigationData) {
      generateDashboard();
    }
  }, [sensorData, irrigationData]);

  if (!dashboardLayout) return <div>Carregando Dashboard...</div>; // Updated loading text

  const DashboardGrid = styled.div`
    display: grid;
    grid-template-columns: ${props => props.theme.breakpoints.md ? '1fr' : dashboardLayout.grid}; /* Responsive grid */
    gap: ${props => props.theme.spacing.large}; /* Use theme spacing */
    padding: ${props => props.theme.spacing.large}; /* Use theme spacing */
    background-color: ${props => props.theme.colors.background}; /* Use theme background */
    border-radius: ${props => props.theme.borderRadius};
    box-shadow: ${props => props.theme.boxShadow};
  `;

  const AIWidget = ({ config, data, styling }) => {
    const StyledWidget = styled.div`
      background: ${props => props.theme.colors.white || styling.background}; /* Use theme white or fallback */
      border-radius: ${props => props.theme.borderRadius};
      padding: ${props => props.theme.spacing.medium};
      box-shadow: ${props => props.theme.boxShadow};
      transition: ${props => props.theme.transition};
      border: 1px solid ${props => props.theme.colors.border}; /* Add border */

      &:hover {
        transform: translateY(-5px); /* Lift effect on hover */
        box-shadow: 0 8px 15px rgba(0,0,0,0.1); /* Enhanced shadow on hover */
      }

      h2 {
        color: ${props => props.theme.colors.primary}; /* Use theme primary color for titles */
        margin-bottom: ${props => props.theme.spacing.small};
        font-size: 1.25rem;
      }
    `;

    return (
      <StyledWidget>
        <h2>{config.title}</h2>
        {config.visualization === 'chart' ? (
          <SmartChart data={data} context={config.title} />
        ) : (
          data && <pre>{JSON.stringify(data, null, 2)}</pre> // Prettify JSON output
        )}
      </StyledWidget>
    );
  };

  return (
    <DashboardGrid>
      {dashboardLayout.widgets.map(widget => (
        <AIWidget
          key={widget.id}
          config={widget}
          data={widget.dataSource === 'sensors' ? sensorData : irrigationData}
          styling={widget.aiStyling}
        />
      ))}
    </DashboardGrid>
  );
};

export default IrrigationDashboard; // Renamed export
