import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import './IrrigationDashboard.css';

const DashboardContainer = styled.div`
  padding: 2rem;
`;

const FormSection = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  max-width: 500px;
`;

const InputGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  font-weight: bold;
  margin-bottom: 4px;
`;

const Select = styled.select`
  padding: 0.5rem;
`;

const Input = styled.input`
  padding: 0.5rem;
`;

const Button = styled.button`
  padding: 0.75rem;
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  border: none;
  border-radius: 4px;
  font-weight: bold;
  cursor: pointer;
`;

const ResultBox = styled.div`
  margin-top: 2rem;
  background: ${({ theme }) => theme.colors.cardBackground || '#f9f9f9'};
  padding: 1rem;
  border-radius: 8px;
  border: 1px solid #ccc;
`;

const IrrigationDashboard = () => {
  const [formData, setFormData] = useState({
    temperatura: '',
    umidade: '',
    umidade_solo: '',
    cultura: '',
    tipo_solo: '',
    estagio_planta: ''
  });

  const [options, setOptions] = useState({ culturas: [], tipos_solo: [], estagios_planta: [] });
  const [resultado, setResultado] = useState(null);
  const [erro, setErro] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/opcoes')
      .then(res => res.json())
      .then(data => setOptions(data))
      .catch(err => setErro('Erro ao carregar opções.'));
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setErro(null);
    setResultado(null);

    try {
      const res = await fetch('http://localhost:5000/prever', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await res.json();
      if (data.erro) {
        setErro(data.erro);
      } else {
        setResultado(data.resultado);
      }
    } catch (err) {
      setErro('Erro ao conectar com o servidor.');
    }
  };

  return (
    <DashboardContainer>
      <h1>Previsão de Irrigação 🌾</h1>
      <FormSection onSubmit={handleSubmit}>
        <InputGroup>
          <Label>Temperatura (°C)</Label>
          <Input type="number" name="temperatura" value={formData.temperatura} onChange={handleChange} required />
        </InputGroup>

        <InputGroup>
          <Label>Umidade do Ar (%)</Label>
          <Input type="number" name="umidade" value={formData.umidade} onChange={handleChange} required />
        </InputGroup>

        <InputGroup>
          <Label>Umidade do Solo (%)</Label>
          <Input type="number" name="umidade_solo" value={formData.umidade_solo} onChange={handleChange} required />
        </InputGroup>

        <InputGroup>
          <Label>Cultura</Label>
          <Select name="cultura" value={formData.cultura} onChange={handleChange} required>
            <option value="">Selecione...</option>
            {options.culturas.map(c => (
              <option key={c} value={c}>{c}</option>
            ))}
          </Select>
        </InputGroup>

        <InputGroup>
          <Label>Tipo de Solo</Label>
          <Select name="tipo_solo" value={formData.tipo_solo} onChange={handleChange} required>
            <option value="">Selecione...</option>
            {options.tipos_solo.map(s => (
              <option key={s} value={s}>{s}</option>
            ))}
          </Select>
        </InputGroup>

        <InputGroup>
          <Label>Estágio da Planta</Label>
          <Select name="estagio_planta" value={formData.estagio_planta} onChange={handleChange} required>
            <option value="">Selecione...</option>
            {options.estagios_planta.map(e => (
              <option key={e} value={e}>{e}</option>
            ))}
          </Select>
        </InputGroup>

        <Button type="submit">Enviar</Button>
      </FormSection>

      {erro && <ResultBox style={{ color: 'red' }}>{erro}</ResultBox>}

      {resultado && (
        <ResultBox>
          <h3>Resultado</h3>
          <p><strong>Recomendação:</strong> {resultado.recomendacao}</p>
          <p><strong>Confiança:</strong> {resultado.confianca * 100}%</p>
          <p><strong>Detalhes:</strong></p>
          <ul>
            <li>Temperatura: {resultado.dados_entrada.temperatura}°C</li>
            <li>Umidade do ar: {resultado.dados_entrada.umidade}%</li>
            <li>Umidade do solo: {resultado.dados_entrada.umidade_solo}%</li>
            <li>Cultura: {resultado.dados_entrada.cultura}</li>
            <li>Solo: {resultado.dados_entrada.tipo_solo}</li>
            <li>Estágio: {resultado.dados_entrada.estagio_planta}</li>
          </ul>
        </ResultBox>
      )}
    </DashboardContainer>
  );
};

export default IrrigationDashboard;
