import React, { useState, useEffect, useRef } from 'react';
import './Chatbot.css';

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState('');
    const [loading, setLoading] = useState(false);
    const messagesContainerRef = useRef(null);
    const API_URL = 'http://127.0.0.1:5000/ask';

    useEffect(() => {
        // Focus input on component mount
        document.getElementById('userInput')?.focus();

        // Scroll to bottom on message change
        scrollToBottom();
    }, [messages]);

    const scrollToBottom = () => {
        messagesContainerRef.current?.scrollTo({
            top: messagesContainerRef.current.scrollHeight,
            behavior: 'smooth'
        });
    };

    const handleInputChange = (e) => {
        setUserInput(e.target.value);
        e.target.style.height = 'auto';
        e.target.style.height = Math.min(e.target.scrollHeight, 120) + 'px';
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            sendMessage();
        }
    };

    const sendMessage = async () => {
        const message = userInput.trim();
        if (!message) return;

        // Add user message
        addMessage(message, true);
        setUserInput('');

        // Show loading
        setLoading(true);

        try {
            const response = await fetch(API_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    pergunta: message
                })
            });

            const data = await response.json();

            if (response.ok) {
                addMessage(data.resposta);
            } else {
                addMessage(`‚ùå Erro: ${data.erro || 'Algo deu errado. Tente novamente.'}`);
            }
        } catch (error) {
            console.error('Erro na requisi√ß√£o:', error);
            addMessage('‚ùå Erro de conex√£o. Verifique se a API est√° rodando e tente novamente.');
        }

        setLoading(false);
    };

    const addMessage = (content, isUser = false) => {
        setMessages(prevMessages => {
            const newMessages = [...prevMessages];
            
            // Remove welcome message if it exists
            if (newMessages.length === 0) {
                newMessages.push({ type: 'welcome' });
            }
            
            newMessages.push({
                content: content,
                isUser: isUser,
                type: 'message'
            });
            
            return newMessages;
        });
    };

    const sendSuggestion = (text) => {
        setUserInput(text);
        sendMessage();
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>üöú Seu Parceiro na Fazenda</h2>
                <p>Especialista em caf√©, irriga√ß√£o e tecnologia IoT</p>
            </div>

            <div className="messages-container" id="messagesContainer" ref={messagesContainerRef}>
                {messages.length === 0 ? (
                    <div className="welcome-message">
                        <h3>üëã Ol√°! Sou o AgroBot</h3>
                        <p>Estou aqui para ajudar com suas d√∫vidas sobre lavouras de caf√©, irriga√ß√£o, IoT e muito mais!</p>
                        <div className="suggestions">
                            <div className="suggestion-chip" onClick={() => sendSuggestion('Como melhorar a irriga√ß√£o do caf√©?')}>üíß Irriga√ß√£o do caf√©</div>
                            <div className="suggestion-chip" onClick={() => sendSuggestion('Qual a melhor √©poca para plantar caf√©?')}>‚òï Plantio de caf√©</div>
                            <div className="suggestion-chip" onClick={() => sendSuggestion('Como usar IoT na fazenda?')}>üì° IoT na agricultura</div>
                            <div className="suggestion-chip" onClick={() => sendSuggestion('Dicas para economizar recursos na fazenda')}>üí∞ Economia de recursos</div>
                        </div>
                    </div>
                ) : (
                    messages.map((message, index) => (
                        message.type === 'welcome' ? null : (
                            <div className={`message ${message.isUser ? 'user' : 'bot'}`} key={index}>
                                <div className="message-avatar">{message.isUser ? 'üë§' : 'ü§ñ'}</div>
                                <div className="message-content" dangerouslySetInnerHTML={{ __html: message.content.replace(/\n/g, '<br>') }} />
                            </div>
                        )
                    ))
                )}
            </div>

            <div className="loading" id="loadingIndicator" style={{ display: loading ? 'flex' : 'none' }}>
                <span>AgroBot est√° pensando</span>
                <div className="loading-dots">
                    <div className="loading-dot"></div>
                    <div className="loading-dot"></div>
                    <div className="loading-dot"></div>
                </div>
            </div>

            <div className="input-container">
                <form className="input-form" id="chatForm" onSubmit={(e) => { e.preventDefault(); sendMessage(); }}>
                    <div className="input-wrapper">
                        <textarea
                            id="userInput"
                            placeholder="Digite sua pergunta sobre agricultura, caf√© ou IoT..."
                            rows="1"
                            value={userInput}
                            onChange={handleInputChange}
                            onKeyDown={handleKeyDown}
                        />
                    </div>
                    <button type="submit" id="sendButton" disabled={loading}>
                        {loading ? '‚è≥' : '‚û§'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Chatbot;
