import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './IrrigationForm.css';

const IrrigationForm = () => {
  const [form, setForm] = useState({
    temperatura: '',
    umidade: '',
    umidade_solo: '',
    cultura: '',
    tipo_solo: '',
    estagio_planta: ''
  });

  const [opcoes, setOpcoes] = useState({
    culturas: [],
    tipos_solo: [],
    estagios_planta: []
  });

  const [resultado, setResultado] = useState(null);
  const [erro, setErro] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/opcoes')
      .then(res => setOpcoes(res.data))
      .catch(() => setErro("Erro ao buscar op√ß√µes da API."));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResultado(null);
    setErro(null);
    try {
      const response = await axios.post('http://localhost:5000/prever', form);
      setResultado(response.data.resultado);
    } catch (err) {
      setErro(err.response?.data?.erro || 'Erro ao fazer previs√£o.');
    }
  };

  return (
    <div className="irrigation-form-container">
      <h2>Previs√£o de Irriga√ß√£o üå±</h2>
      <form onSubmit={handleSubmit} className="irrigation-form">
        <label>Temperatura (¬∞C):
          <input type="number" name="temperatura" value={form.temperatura} onChange={handleChange} required />
        </label>

        <label>Umidade do Ar (%):
          <input type="number" name="umidade" value={form.umidade} onChange={handleChange} required />
        </label>

        <label>Umidade do Solo (%):
          <input type="number" name="umidade_solo" value={form.umidade_solo} onChange={handleChange} required />
        </label>

        <label>Cultura:
          <select name="cultura" value={form.cultura} onChange={handleChange} required>
            <option value="">Selecione...</option>
            {opcoes.culturas.map((cultura) => (
              <option key={cultura} value={cultura}>{cultura}</option>
            ))}
          </select>
        </label>

        <label>Tipo de Solo:
          <select name="tipo_solo" value={form.tipo_solo} onChange={handleChange} required>
            <option value="">Selecione...</option>
            {opcoes.tipos_solo.map((solo) => (
              <option key={solo} value={solo}>{solo}</option>
            ))}
          </select>
        </label>

        <label>Est√°gio da Planta:
          <select name="estagio_planta" value={form.estagio_planta} onChange={handleChange} required>
            <option value="">Selecione...</option>
            {opcoes.estagios_planta.map((estagio) => (
              <option key={estagio} value={estagio}>{estagio}</option>
            ))}
          </select>
        </label>

        <button type="submit">Prever Irriga√ß√£o</button>
      </form>

      {erro && <div className="error-box">‚ùå {erro}</div>}
      {resultado && (
        <div className="result-box">
          <h3>{resultado.recomendacao}</h3>
          <p><strong>Confian√ßa:</strong> {resultado.confianca * 100}%</p>
          <pre>{JSON.stringify(resultado.dados_entrada, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default IrrigationForm;
