import React, { useState, useEffect, useRef } from 'react';
import { Send, Leaf, User, Bot, Sparkles, Sun, Droplets, MessageCircle, ArrowDown } from 'lucide-react';

const AgroChat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Ol√°! Sou o AgroAssistente, seu consultor agr√≠cola digital. Como posso ajud√°-lo hoje? üå±",
      type: 'bot',
      timestamp: new Date()
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showScrollButton, setShowScrollButton] = useState(false);
  const messagesEndRef = useRef(null);
  const messagesContainerRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleScroll = () => {
    if (messagesContainerRef.current) {
      const { scrollTop, scrollHeight, clientHeight } = messagesContainerRef.current;
      const isNearBottom = scrollHeight - scrollTop - clientHeight < 100;
      setShowScrollButton(!isNearBottom);
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const simulateBotResponse = (userMessage) => {
    const responses = [
      "Baseado na sua quest√£o sobre cultivo, recomendo verificar o pH do solo primeiro. Um solo equilibrado √© fundamental para o crescimento saud√°vel das plantas. üå±",
      "Para essa situa√ß√£o, sugiro implementar um sistema de irriga√ß√£o por gotejamento. √â mais eficiente e economiza √°gua. üíß",
      "Considerando as condi√ß√µes clim√°ticas atuais, √© importante monitorar a umidade do solo. Posso ajud√°-lo com t√©cnicas de manejo adequadas. üå°Ô∏è",
      "Essa √© uma excelente pergunta sobre agricultura sustent√°vel. Vamos trabalhar juntos para encontrar a melhor solu√ß√£o para sua propriedade. üöú",
      "Com base na an√°lise dos dados, recomendo ajustar o cronograma de plantio. O timing √© crucial para maximizar a produtividade. üìä"
    ];
    
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: inputValue,
      type: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsTyping(true);

    setTimeout(() => {
      const botResponse = {
        id: Date.now() + 1,
        text: simulateBotResponse(inputValue),
        type: 'bot',
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, botResponse]);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const quickSuggestions = [
    { text: "Como melhorar o solo?", icon: "üå±" },
    { text: "Dicas de irriga√ß√£o", icon: "üíß" },
    { text: "Controle de pragas", icon: "üêõ" },
    { text: "Melhor √©poca para plantar", icon: "üìÖ" },
    { text: "Fertiliza√ß√£o org√¢nica", icon: "üåø" },
    { text: "Rota√ß√£o de culturas", icon: "üîÑ" }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-emerald-50 to-teal-50 p-4 flex items-center justify-center">
      {/* Floating background elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-10 left-10 opacity-10 animate-pulse">
          <Leaf size={120} className="text-green-600" />
        </div>
        <div className="absolute bottom-20 right-10 opacity-10 animate-pulse" style={{animationDelay: '1s'}}>
          <Sun size={100} className="text-yellow-500" />
        </div>
        <div className="absolute top-1/2 right-20 opacity-10 animate-pulse" style={{animationDelay: '2s'}}>
          <Droplets size={80} className="text-blue-500" />
        </div>
      </div>

      {/* Main chat container */}
      <div className="w-full max-w-4xl h-[90vh] flex flex-col bg-white/80 backdrop-blur-lg rounded-3xl shadow-2xl border border-white/20 overflow-hidden">
        
        {/* Enhanced Header */}
        <div className="bg-gradient-to-r from-green-600 to-emerald-600 p-6 text-white relative overflow-hidden">
          <div className="absolute inset-0 bg-black/10"></div>
          <div className="relative z-10 flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="bg-white/20 backdrop-blur-sm rounded-full p-3 ring-2 ring-white/30">
                <Leaf size={24} className="text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold">AgroChat</h1>
                <p className="text-green-100 text-sm">Seu Consultor Agr√≠cola Inteligente</p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse"></div>
              <span className="text-sm text-green-100">Online</span>
            </div>
          </div>
        </div>

        {/* Messages area with enhanced styling */}
        <div 
          ref={messagesContainerRef}
          onScroll={handleScroll}
          className="flex-1 overflow-y-auto p-6 space-y-6 bg-gradient-to-b from-green-50/50 to-white/50 backdrop-blur-sm scrollbar-thin scrollbar-thumb-green-200 scrollbar-track-transparent"
        >
          {messages.map((message, index) => (
            <div
              key={message.id}
              className={`flex items-start space-x-3 animate-in slide-in-from-bottom-5 duration-500 ${
                message.type === 'user' ? 'justify-end' : 'justify-start'
              }`}
              style={{animationDelay: `${index * 100}ms`}}
            >
              {message.type === 'bot' && (
                <div className="flex-shrink-0 bg-gradient-to-br from-green-500 to-emerald-600 rounded-full p-3 shadow-lg ring-2 ring-green-100">
                  <Bot size={18} className="text-white" />
                </div>
              )}
              
              <div
                className={`max-w-xs lg:max-w-md px-5 py-3 rounded-2xl shadow-lg transform transition-all duration-300 hover:scale-105 ${
                  message.type === 'user'
                    ? 'bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-br-sm shadow-blue-200'
                    : 'bg-white/90 backdrop-blur-sm text-gray-800 rounded-bl-sm shadow-green-100 border border-green-100'
                }`}
              >
                <p className="text-sm leading-relaxed">{message.text}</p>
                <p className={`text-xs mt-2 ${
                  message.type === 'user' ? 'text-blue-100' : 'text-gray-500'
                }`}>
                  {formatTime(message.timestamp)}
                </p>
              </div>

              {message.type === 'user' && (
                <div className="flex-shrink-0 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full p-3 shadow-lg ring-2 ring-blue-100">
                  <User size={18} className="text-white" />
                </div>
              )}
            </div>
          ))}

          {/* Enhanced typing indicator */}
          {isTyping && (
            <div className="flex items-start space-x-3 animate-pulse">
              <div className="flex-shrink-0 bg-gradient-to-br from-green-500 to-emerald-600 rounded-full p-3 shadow-lg">
                <Bot size={18} className="text-white" />
              </div>
              <div className="bg-white/90 backdrop-blur-sm border border-green-100 rounded-2xl rounded-bl-sm px-5 py-3 shadow-lg">
                <div className="flex items-center space-x-2">
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce"></div>
                    <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                    <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                  </div>
                  <span className="text-xs text-gray-500 ml-2">AgroAssistente est√° digitando...</span>
                </div>
              </div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        {/* Scroll to bottom button */}
        {showScrollButton && (
          <button
            onClick={scrollToBottom}
            className="absolute right-6 bottom-32 bg-green-500 hover:bg-green-600 text-white p-2 rounded-full shadow-lg transition-all duration-300 hover:scale-110 z-10"
          >
            <ArrowDown size={20} />
          </button>
        )}

        {/* Enhanced input area */}
        <div className="bg-white/90 backdrop-blur-lg border-t border-green-100 p-6">
          {/* Quick suggestions */}
          <div className="mb-4">
            <p className="text-sm text-gray-600 mb-3 font-medium">Sugest√µes r√°pidas:</p>
            <div className="flex flex-wrap gap-2">
              {quickSuggestions.map((suggestion, index) => (
                <button
                  key={index}
                  onClick={() => setInputValue(suggestion.text)}
                  className="flex items-center space-x-1 px-3 py-2 bg-gradient-to-r from-green-100 to-emerald-100 hover:from-green-200 hover:to-emerald-200 text-green-800 text-xs rounded-full transition-all duration-300 border border-green-200 hover:scale-105 shadow-sm"
                  disabled={isTyping}
                >
                  <span>{suggestion.icon}</span>
                  <span>{suggestion.text}</span>
                </button>
              ))}
            </div>
          </div>

          {/* Input field */}
          <div className="flex space-x-4 items-end">
            <div className="flex-1 relative">
              <div className="relative">
                <textarea
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Digite sua pergunta sobre agricultura..."
                  className="w-full px-4 py-3 pr-20 bg-gray-50/80 backdrop-blur-sm border border-gray-200 rounded-2xl focus:outline-none focus:ring-2 focus:ring-green-400 focus:border-transparent resize-none transition-all duration-300 text-gray-800 placeholder-gray-500 shadow-sm"
                  rows="2"
                  disabled={isTyping}
                />
                <div className="absolute bottom-2 right-2 text-xs text-gray-400 bg-white/80 px-2 py-1 rounded-lg">
                  {inputValue.length}/500
                </div>
              </div>
              <p className="text-xs text-gray-500 mt-1">
                Pressione Enter para enviar ‚Ä¢ Shift + Enter para nova linha
              </p>
            </div>
            
            <button
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isTyping}
              className="bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 disabled:from-gray-300 disabled:to-gray-400 text-white p-4 rounded-2xl transition-all duration-300 shadow-lg disabled:cursor-not-allowed transform hover:scale-110 active:scale-95"
            >
              {isTyping ? (
                <div className="animate-spin">
                  <Sparkles size={20} />
                </div>
              ) : (
                <Send size={20} />
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Custom styles */}
      <style jsx>{`
        @keyframes animate-in {
          from {
            opacity: 0;
            transform: translateY(10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-in {
          animation: animate-in 0.5s ease-out forwards;
        }
        
        .slide-in-from-bottom-5 {
          animation: slide-in-from-bottom-5 0.5s ease-out;
        }
        
        @keyframes slide-in-from-bottom-5 {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .scrollbar-thin::-webkit-scrollbar {
          width: 6px;
        }
        
        .scrollbar-thumb-green-200::-webkit-scrollbar-thumb {
          background-color: rgb(187 247 208);
          border-radius: 10px;
        }
        
        .scrollbar-thumb-green-200::-webkit-scrollbar-thumb:hover {
          background-color: rgb(134 239 172);
        }
        
        .scrollbar-track-transparent::-webkit-scrollbar-track {
          background: transparent;
        }
      `}</style>
    </div>
  );
};

export default AgroChat;
