import React, { useState, useEffect, useRef } from 'react';
import { Send, Leaf, User, Bot, Sparkles, Sun, Droplets, MessageCircle, ArrowDown, AreaChart, TrendingUp, BarChart3 } from 'lucide-react';
import PlotCard from './PlotCard'; // Import PlotCard
import '../components/Chatbot.css'; // Import the CSS file

const AgroChat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Olá! Sou o AgroAssistente, seu consultor agrícola digital. Como posso ajudá-lo hoje? 🌱",
      type: 'bot',
      timestamp: new Date()
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showScrollButton, setShowScrollButton] = useState(false);
  const messagesEndRef = useRef(null);
  const messagesContainerRef = useRef(null);
  const [activeTab, setActiveTab] = useState('chat'); // New state for active tab
  const [suggestions, setSuggestions] = useState([]); // New state for smart suggestions

  // Mock data for plots and charts
  const mockPlots = [
    { id: 1, name: 'Lote 1 - Milho', status: 'Crescimento', health: 'Excelente', area: '2.5 ha', imageUrl: 'https://via.placeholder.com/150/a8e6cf/000000?text=Milho' },
    { id: 2, name: 'Lote 2 - Soja', status: 'Plantio', health: 'Bom', area: '3.0 ha', imageUrl: 'https://via.placeholder.com/150/d4f8e8/000000?text=Soja' },
    { id: 3, name: 'Lote 3 - Trigo', status: 'Colheita', health: 'Ótimo', area: '2.0 ha', imageUrl: 'https://via.placeholder.com/150/b2f0d4/000000?text=Trigo' },
  ];

  const mockChartData = [
    { time: 'Jan', value: 65 },
    { time: 'Fev', value: 59 },
    { time: 'Mar', value: 80 },
    { time: 'Abr', value: 81 },
    { time: 'Mai', value: 56 },
    { time: 'Jun', value: 70 },
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleScroll = () => {
    if (messagesContainerRef.current) {
      const { scrollTop, scrollHeight, clientHeight } = messagesContainerRef.current;
      const isNearBottom = scrollHeight - scrollTop - clientHeight < 100;
      setShowScrollButton(!isNearBottom);
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const simulateBotResponse = (userMessage) => {
    const responses = [
      "Baseado na sua questão sobre cultivo, recomendo verificar o pH do solo primeiro. Um solo equilibrado é fundamental para o crescimento saudável das plantas. 🌱",
      "Para essa situação, sugiro implementar um sistema de irrigação por gotejamento. É mais eficiente e economiza água. 💧",
      "Considerando as condições climáticas atuais, é importante monitorar a umidade do solo. Posso ajudá-lo com técnicas de manejo adequadas. 🌡️",
      "Essa é uma excelente pergunta sobre agricultura sustentável. Vamos trabalhar juntos para encontrar a melhor solução para sua propriedade. 🚜",
      "Com base na análise dos dados, recomendo ajustar o cronograma de plantio. O timing é crucial para maximizar a produtividade. 📊"
    ];
    
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: inputValue,
      type: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsTyping(true);

    setTimeout(() => {
      const botResponse = {
        id: Date.now() + 1,
        text: simulateBotResponse(inputValue),
        type: 'bot',
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, botResponse]);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const quickSuggestions = [
    { text: "Como melhorar o solo?", icon: "🌱" },
    { text: "Dicas de irrigação", icon: "💧" },
    { text: "Controle de pragas", icon: "🐛" },
    { text: "Melhor época para plantar", icon: "📅" },
    { text: "Fertilização orgânica", icon: "🌿" },
    { text: "Rotação de culturas", icon: "🔄" }
  ];

  const SimpleChart = ({ data }) => (
    <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-200">
      <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
        <BarChart3 size={20} className="text-green-600" />
        Produtividade Mensal
      </h3>
      <div className="space-y-3">
        {data.map((item, index) => (
          <div key={index} className="flex items-center justify-between">
            <span className="text-sm font-medium text-gray-600">{item.time}</span>
            <div className="flex items-center gap-3">
              <div className="w-32 bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-green-500 to-emerald-600 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${(item.value / 100) * 100}%` }}
                ></div>
              </div>
              <span className="text-sm font-semibold text-green-600">{item.value}%</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-emerald-50 to-teal-50 p-4 flex items-center justify-center">
      {/* Floating background elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-10 left-10 opacity-10 animate-pulse">
          <Leaf size={120} className="text-green-600" />
        </div>
        <div className="absolute bottom-20 right-10 opacity-10 animate-pulse" style={{animationDelay: '1s'}}>
          <Sun size={100} className="text-yellow-500" />
        </div>
        <div className="absolute top-1/2 right-20 opacity-10 animate-pulse" style={{animationDelay: '2s'}}>
          <Droplets size={80} className="text-blue-500" />
        </div>
      </div>

      {/* Main container */}
      <div className="w-full max-w-7xl h-[90vh] flex flex-col bg-white/80 backdrop-blur-lg rounded-3xl shadow-2xl border border-white/20 overflow-hidden">
        
        {/* Enhanced Header with Tabs */}
        <div className="bg-gradient-to-r from-green-600 to-emerald-600 p-6 text-white relative overflow-hidden">
          <div className="absolute inset-0 bg-black/10"></div>
          <div className="relative z-10">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-4">
                <div className="bg-white/20 backdrop-blur-sm rounded-full p-3 ring-2 ring-white/30">
                  <Leaf size={24} className="text-white" />
                </div>
                <div>
                  <h1 className="text-2xl font-bold">AgroChat Pro</h1>
                  <p className="text-green-100 text-sm">Sua Plataforma Agrícola Inteligente</p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse"></div>
                <span className="text-sm text-green-100">Sistema Online</span>
              </div>
            </div>
            
            {/* Navigation Tabs */}
            <div className="flex space-x-1 bg-white/10 rounded-lg p-1">
              {[
                { id: 'chat', label: 'Chat IA', icon: MessageCircle },
                { id: 'plots', label: 'Meus Lotes', icon: AreaChart },
                { id: 'analytics', label: 'Análises', icon: TrendingUp }
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-md transition-all duration-300 ${
                    activeTab === tab.id 
                      ? 'bg-white text-green-600 shadow-md' 
                      : 'text-white/80 hover:text-white hover:bg-white/10'
                  }`}
                  aria-controls={`${tab.id}-panel`}
                  role="tab"
                  aria-selected={activeTab === tab.id}
                  tabIndex={activeTab === tab.id ? 0 : -1}
                >
                  <tab.icon size={16} />
                  {tab.label}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Content Area */}
        <div className="flex-1 overflow-hidden">
          
          {/* Chat Tab */}
          {activeTab === 'chat' && (
            <div className="h-full flex flex-col" id="chat-panel" role="tabpanel" aria-labelledby="chat">
              {/* Messages area */}
              <div 
                ref={messagesContainerRef}
                onScroll={handleScroll}
                className="flex-1 overflow-y-auto p-6 space-y-6 bg-gradient-to-b from-green-50/50 to-white/50 custom-scrollbar"
              >
                {messages.map((message, index) => (
                  <div
                    key={message.id}
                    className={`flex items-start space-x-3 ${
                      message.type === 'user' ? 'justify-end animate-slide-in-right' : 'justify-start animate-slide-in-left'
                    }`}
                    style={{animationDelay: `${index * 50}ms`}}
                  >
                    {message.type === 'bot' && (
                      <div className="flex-shrink-0 bg-gradient-to-br from-green-500 to-emerald-600 rounded-full p-3 shadow-lg ring-2 ring-green-100">
                        <Bot size={18} className="text-white" aria-label="AgroAssistente" />
                      </div>
                    )}
                    
                    <div
                      className={`max-w-xs lg:max-w-md px-5 py-3 rounded-2xl shadow-lg transform transition-all duration-300 hover:scale-105 ${
                        message.type === 'user'
                          ? 'bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-br-sm shadow-blue-200'
                          : 'card-elevated text-gray-800 rounded-bl-sm'
                      }`}
                      role="status"
                      aria-live="polite"
                    >
                      <p className="text-sm leading-relaxed">{message.text}</p>
                      <p className={`text-xs mt-2 ${
                        message.type === 'user' ? 'text-blue-100' : 'text-gray-500'
                      }`}>
                        {formatTime(message.timestamp)}
                      </p>
                    </div>

                    {message.type === 'user' && (
                      <div className="flex-shrink-0 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full p-3 shadow-lg ring-2 ring-blue-100">
                        <User size={18} className="text-white" aria-label="Você" />
                      </div>
                    )}
                  </div>
                ))}

                {isTyping && (
                  <div className="flex items-start space-x-3 animate-fade-in-up" role="status" aria-live="polite">
                    <div className="flex-shrink-0 bg-gradient-to-br from-green-500 to-emerald-600 rounded-full p-3 shadow-lg">
                      <Bot size={18} className="text-white" aria-label="AgroAssistente" />
                    </div>
                    <div className="card-elevated rounded-bl-sm px-5 py-3 shadow-lg">
                      <div className="flex items-center space-x-2">
                        <div className="flex space-x-1">
                          <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0s'}}></div>
                          <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                          <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                        </div>
                        <span className="text-xs text-gray-500 ml-2">AgroAssistente está digitando...</span>
                      </div>
                    </div>
                  </div>
                )}
                
                <div ref={messagesEndRef} />
              </div>

              {/* Input area */}
              <div className="bg-white/90 backdrop-blur-lg border-t border-green-100 p-6">
                {/* Quick suggestions */}
                <div className="mb-4">
                  <p className="text-sm text-gray-600 mb-3 font-medium">Sugestões rápidas:</p>
                  <div className="flex flex-wrap gap-2">
                    {quickSuggestions.map((suggestion, index) => (
                      <button
                        key={index}
                        onClick={() => setInputValue(suggestion.text)}
                        className="flex items-center space-x-1 px-3 py-2 bg-gradient-to-r from-green-100 to-emerald-100 hover:from-green-200 hover:to-emerald-200 text-green-800 text-xs rounded-full transition-all duration-300 border border-green-200 hover:scale-105 shadow-sm focus-ring"
                        disabled={isTyping}
                        aria-label={`Sugestão rápida: ${suggestion.text}`}
                      >
                        <span>{suggestion.icon}</span>
                        <span>{suggestion.text}</span>
                      </button>
                    ))}
                  </div>
                </div>

                {/* Input field with smart suggestions */}
                <div className="flex space-x-4 items-end relative">
                  <div className="flex-1 relative">
                    <textarea
                      value={inputValue}
                      onChange={(e) => {
                        setInputValue(e.target.value);
                        // Simulate smart suggestions based on input
                        if (e.target.value.toLowerCase().includes('solo')) {
                          setSuggestions(['Análise de solo', 'Melhorar fertilidade do solo', 'Correção de pH do solo']);
                        } else if (e.target.value.toLowerCase().includes('irrigação')) {
                          setSuggestions(['Sistemas de irrigação', 'Eficiência hídrica', 'Manejo de irrigação']);
                        } else {
                          setSuggestions([]);
                        }
                      }}
                      onKeyPress={handleKeyPress}
                      placeholder="Digite sua pergunta sobre agricultura..."
                      className="w-full px-4 py-3 pr-20 bg-gray-50/80 border border-gray-200 rounded-2xl focus:outline-none focus:ring-2 focus:ring-green-400 focus:border-transparent resize-none transition-all duration-300 text-gray-800 placeholder-gray-500 shadow-sm focus-ring"
                      rows="2"
                      disabled={isTyping}
                      aria-label="Campo de entrada de mensagem"
                      maxLength="500"
                    />
                    
                    {/* Smart suggestions dropdown */}
                    {suggestions.length > 0 && (
                      <div className="absolute bottom-full left-0 right-0 mb-2 bg-white border border-gray-200 rounded-xl shadow-lg z-10 max-h-48 overflow-y-auto custom-scrollbar">
                        {suggestions.map((suggestion, index) => (
                          <button
                            key={index}
                            onClick={() => {
                              setInputValue(suggestion);
                              setSuggestions([]);
                            }}
                            className="w-full text-left px-4 py-3 hover:bg-green-50 transition-colors duration-200 border-b border-gray-100 last:border-b-0 focus-ring"
                            aria-label={`Sugestão: ${suggestion}`}
                          >
                            <span className="text-sm text-gray-700">{suggestion}</span>
                          </button>
                        ))}
                      </div>
                    )}

                    <div className="absolute bottom-2 right-2 text-xs text-gray-400 bg-white/80 px-2 py-1 rounded-lg">
                      {inputValue.length}/500
                    </div>
                  </div>
                  
                  <button
                    onClick={handleSendMessage}
                    disabled={!inputValue.trim() || isTyping}
                    className="bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 disabled:from-gray-300 disabled:to-gray-400 text-white p-4 rounded-2xl transition-all duration-300 shadow-lg disabled:cursor-not-allowed transform hover:scale-110 active:scale-95 focus-ring"
                    aria-label="Enviar mensagem"
                  >
                    {isTyping ? (
                      <div className="animate-spin">
                        <Sparkles size={20} />
                      </div>
                    ) : (
                      <Send size={20} />
                    )}
                  </button>
                </div>
                <p className="text-xs text-gray-500 mt-1">
                  Pressione Enter para enviar • Shift + Enter para nova linha
                </p>
              </div>
            </div>
          )}

          {/* Plots Tab */}
          {activeTab === 'plots' && (
            <div className="h-full overflow-y-auto p-6 bg-gradient-to-b from-green-50/50 to-white/50 custom-scrollbar" id="plots-panel" role="tabpanel" aria-labelledby="plots">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-gray-800 mb-2">Gestão de Lotes</h2>
                <p className="text-gray-600">Monitore e gerencie seus lotes agrícolas</p>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {mockPlots.map((plot) => (
                  <PlotCard key={plot.id} plot={plot} />
                ))}
              </div>

              {/* Add new plot button */}
              <div className="mt-8 flex justify-center">
                <button className="bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white px-6 py-3 rounded-xl shadow-lg transition-all duration-300 hover:scale-105 focus-ring">
                  + Adicionar Novo Lote
                </button>
              </div>
            </div>
          )}

          {/* Analytics Tab */}
          {activeTab === 'analytics' && (
            <div className="h-full overflow-y-auto p-6 bg-gradient-to-b from-green-50/50 to-white/50 custom-scrollbar" id="analytics-panel" role="tabpanel" aria-labelledby="analytics">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-gray-800 mb-2">Análises e Relatórios</h2>
                <p className="text-gray-600">Dados inteligentes para decisões assertivas</p>
              </div>
              
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <SimpleChart data={mockChartData} />
                
                <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-200">
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Métricas Gerais</h3>
                  <div className="space-y-4">
                    <div className="flex justify-between items-center p-3 bg-green-50 rounded-lg">
                      <span className="text-green-800 font-medium">Total de Hectares</span>
                      <span className="text-green-600 font-bold">7.5 ha</span>
                    </div>
                    <div className="flex justify-between items-center p-3 bg-blue-50 rounded-lg">
                      <span className="text-blue-800 font-medium">Lotes Irrigados</span>
                      <span className="text-blue-600 font-bold">2/3</span>
                    </div>
                    <div className="flex justify-between items-center p-3 bg-yellow-50 rounded-lg">
                      <span className="text-yellow-800 font-medium">Produtividade Média</span>
                      <span className="text-yellow-600 font-bold">84%</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Scroll to bottom button for chat */}
        {activeTab === 'chat' && showScrollButton && (
          <button
            onClick={scrollToBottom}
            className="absolute right-6 bottom-32 bg-green-500 hover:bg-green-600 text-white p-2 rounded-full shadow-lg transition-all duration-300 hover:scale-110 z-10 focus-ring"
            aria-label="Rolar para o final da conversa"
          >
            <ArrowDown size={20} />
          </button>
        )}
      </div>
    </div>
  );
};

export default AgroChat;
