import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import AIThemeGenerator from './services/AIThemeGenerator';
import SmartChatbot from './components/Chatbot';
import AIDashboard from './components/IrrigationDashboard'; // Assuming you will create this component
import './App.css';
import Navigation from './components/Navigation';
import IrrigationDashboard from './components/IrrigationDashboard';
import Chatbot from './components/Chatbot';
import Iot from './components/Iot';

const App = () => {
const aiTheme = {
  colors: {
    primary: '#22c55e',
    secondary: '#a0cae5',
    background: '#f0f9ff',
    text: '#333',
  },
  fonts: {
    primary: 'Roboto, sans-serif',
  },
  borderRadius: '8px',
};

const GlobalStyle = createGlobalStyle`
  body {
    font-family: ${aiTheme.fonts.primary};
    background-color: ${aiTheme.colors.background};
    margin: 0;
    padding: 0;
  }
  
  button {
    border-radius: ${aiTheme.borderRadius};
  }
`;

  return (
    <ThemeProvider theme={aiTheme}>
      <Router>
        <div className="app-container">
          <Navigation />
          <main className="main-content">
            <Routes>
              <Route path="/" element={<SmartChatbot />} />
              <Route path="/irrigation-dashboard" element={<AIDashboard />} />
              <Route path="/Iot" element={<Iot />} />
            </Routes>
          </main>
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
