import React, { useState } from 'react';
import './Chatbot.css';

const Chatbot = () => {
  const [pergunta, setPergunta] = useState('');
  const [mensagens, setMensagens] = useState([]);

  const enviarPergunta = async () => {
    if (!pergunta.trim()) return;

    const novaPergunta = { texto: pergunta, autor: 'usuÃ¡rio' };
    setMensagens((msgs) => [...msgs, novaPergunta]);
    setPergunta('');

    try {
      const response = await fetch('http://localhost:5000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pergunta })
      });

      const data = await response.json();
      const respostaBot = { texto: data.resposta || 'Erro na resposta.', autor: 'bot' };
      setMensagens((msgs) => [...msgs, respostaBot]);

    } catch (error) {
      setMensagens((msgs) => [...msgs, { texto: 'Erro ao conectar com o servidor.', autor: 'bot' }]);
    }
  };

  return (
    <div className="chat-wrapper">
      <div className="chat-card">
        <h2>ðŸ¤– <span>AgroBot</span> - Consultoria para CafÃ©</h2>

        <div className="chat-box">
          {mensagens.map((msg, i) => (
            <div key={i} className={`mensagem ${msg.autor}`}>
              {msg.texto}
            </div>
          ))}
        </div>

        <div className="input-area">
          <input
            type="text"
            value={pergunta}
            onChange={(e) => setPergunta(e.target.value)}
            placeholder="Digite sua pergunta sobre cafÃ©..."
            onKeyDown={(e) => e.key === 'Enter' && enviarPergunta()}
          />
          <button onClick={enviarPergunta}>Enviar</button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
