import React, { useState } from 'react';

function Chatbot() {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');
  
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/ask", { // ‚úÖ porta corrigida aqui
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ pergunta: question }),
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Erro do servidor: ${text}`);
      }

      const data = await res.json();
      setResponse(data.resposta || 'Nenhuma resposta recebida.');
    } catch (error) {
      console.error('Error:', error);
      setResponse('‚ùå Erro: n√£o foi poss√≠vel obter uma resposta do servidor.');
    }
  };

  return (
    <div style={{ padding: '1rem', maxWidth: '500px', margin: '0 auto' }}>
      <h2>AgroBot ü§ñüå±</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Pergunte algo sobre lavouras de caf√©..."
          style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
        />
        <button type="submit" style={{ padding: '0.5rem 1rem' }}>
          Enviar
        </button>
      </form>
      {response && (
        <div style={{ marginTop: '1rem', background: '#f9f9f9', padding: '1rem', borderRadius: '8px' }}>
          <strong>Resposta:</strong>
          <p>{response}</p>
        </div>
      )}
    </div>
  );
}

export default Chatbot;
