import styled from 'styled-components';

const aiModel = {
  designComponent: async ({ type, content, context, style }) => {
    // Mock AI component design
    console.log(`AI designing component of type ${type} with content ${content} and style ${style}`);
    return {
      background: '#ffffff',
      borderRadius: '12px',
      padding: '16px',
      shadow: '0 4px 6px rgba(0,0,0,0.1)',
      animation: 'fadeIn 0.3s ease-in-out',
      aiBackground: '#e5e7eb',
      aiBorder: '#9ca3af',
    };
  },
  designCard: async ({ dataType, importance, visualization, agriculturalContext }) => {
    // Mock AI card design
    console.log(`AI designing card for data type ${dataType} with visualization ${visualization}`);
    return {
      layout: 'grid',
      colors: {
        background: '#ffffff',
        text: '#374151',
      },
      typography: {
        fontFamily: 'Arial, sans-serif',
        fontSize: '14px',
      },
      interactions: {
        hoverTransform: 'scale(1.02)',
        hoverShadow: '0 6px 8px rgba(0,0,0,0.15)',
      },
    };
  },
};

const AIComponentFactory = {
  createChatBubble: async (message, type) => {
    const design = await aiModel.designComponent({
      type: 'chat_bubble',
      content: message,
      context: 'agricultural_assistant',
      style: 'modern_friendly',
    });

    return styled.div`
      background: ${design.background};
      border-radius: ${design.borderRadius};
      padding: ${design.padding};
      box-shadow: ${design.shadow};
      animation: ${design.animation};

      ${type === 'user' ? `
        background: linear-gradient(135deg, #22c55e, #16a34a);
        margin-left: auto;
        color: white;
      ` : `
        background: ${design.aiBackground};
        border: 1px solid ${design.aiBorder};
      `}
    `;
  },

  createDataCard: async (data, visualization) => {
    const cardDesign = await aiModel.designCard({
      dataType: data.type,
      importance: data.priority,
      visualization: visualization,
      agriculturalContext: true,
    });

    return {
      layout: cardDesign.layout,
      colors: cardDesign.colors,
      typography: cardDesign.typography,
      interactions: cardDesign.interactions,
    };
  },
};

export default AIComponentFactory;
