import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import useSmartAPI from '../hooks/useSmartAPI';
import AIComponentFactory from '../services/AIComponentFactory';
import SmartInput from './SmartInput';
import './Chatbot.css';

const aiModel = {
  optimizeChatUI: async ({ messages, userBehavior, deviceType, timeOfDay }) => {
    // Mock AI chat UI optimization
    console.log('AI optimizing chat UI');
    return {
      background: 'linear-gradient(135deg, #f0fdf4, #dcfce7)',
      borderRadius: '20px',
      padding: '24px',
      shadow: '0 20px 50px rgba(0,0,0,0.1)',
    };
  },
};

const SmartChatbot = () => {
  const { data: chatResponse, fetch: sendMessage } = useSmartAPI('chat');
  const [messages, setMessages] = useState([]);
  const [uiOptimizations, setUIOptimizations] = useState({});

  // IA otimiza a UI baseado no contexto da conversa
  useEffect(() => {
    const optimizeUI = async () => {
      const optimizations = await aiModel.optimizeChatUI({
        messages: messages,
        userBehavior: 'someUserBehavior', // Replace with actual user behavior tracking
        deviceType: 'desktop', // Replace with actual device type detection
        timeOfDay: new Date().getHours(),
      });

      setUIOptimizations(optimizations);
    };

    optimizeUI();
  }, [messages]);

  const handleSend = async (input) => {
      await sendMessage({ message: input });
      setMessages([...messages, { text: input, type: 'user' }]);
  };

  useEffect(() => {
    if (chatResponse && chatResponse.response) {
      setMessages([...messages, { text: chatResponse.response, type: 'ai' }]);
    }
  }, [chatResponse]);

  const StyledChatContainer = styled.div`
    background: ${uiOptimizations.background || 'linear-gradient(135deg, #f0fdf4, #dcfce7)'};
    border-radius: ${uiOptimizations.borderRadius || '20px'};
    padding: ${uiOptimizations.padding || '24px'};
    box-shadow: ${uiOptimizations.shadow || '0 20px 50px rgba(0,0,0,0.1)'};
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255,255,255,0.2);
    width: 500px;
    margin: 20px auto;
  `;

  const ChatHeader = styled.div`
    font-size: 24px;
    font-weight: bold;
    color: #333;
    margin-bottom: 20px;
    text-align: center;
  `;

  const MessageList = styled.div`
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 20px;
  `;

  const Message = styled.div`
    padding: 10px 15px;
    border-radius: 20px;
    background: #e2e8f0;
    color: #333;
    align-self: flex-start;

    &.user {
      align-self: flex-end;
      background: #22c55e;
      color: white;
    }
  `;

  return (
    <StyledChatContainer>
      <ChatHeader>AgroChat</ChatHeader>
      <MessageList>
        {messages.map((msg, index) => (
          <Message key={index} className={msg.type}>{msg.text}</Message>
        ))}
      </MessageList>
      <SmartInput onSend={handleSend} />
    </StyledChatContainer>
  );
};

export default SmartChatbot;
