import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import useSmartAPI from '../hooks/useSmartAPI';
import SmartChart from './SmartChart';

const aiModel = {
  createDashboard: async ({ data, style, priority, responsiveness }) => {
    // Mock AI dashboard creation
    console.log('AI creating dashboard');
    return {
      grid: '1fr 1fr',
      widgets: [
        {
          id: 'sensor-data',
          title: 'Sensor Data',
          dataSource: 'sensors',
          visualization: 'table',
          aiStyling: {
            background: '#f0fdf4',
            borderRadius: '12px',
            padding: '16px',
            shadow: '0 4px 6px rgba(0,0,0,0.1)',
          },
        },
        {
          id: 'irrigation-data',
          title: 'Irrigation Data',
          dataSource: 'irrigation',
          visualization: 'chart',
          aiStyling: {
            background: '#e5e7eb',
            borderRadius: '12px',
            padding: '16px',
            shadow: '0 4px 6px rgba(0,0,0,0.1)',
          },
        },
      ],
    };
  },
};

const AIDashboard = () => {
  const { data: sensorData } = useSmartAPI('sensors');
  const { data: irrigationData } = useSmartAPI('irrigation');
  const [dashboardLayout, setDashboardLayout] = useState(null);

  useEffect(() => {
    const generateDashboard = async () => {
      const layout = await aiModel.createDashboard({
        data: { sensors: sensorData, irrigation: irrigationData },
        style: 'modern_agricultural',
        priority: 'visual_impact',
        responsiveness: 'mobile_first',
      });

      setDashboardLayout(layout);
    };

    if (sensorData && irrigationData) {
      generateDashboard();
    }
  }, [sensorData, irrigationData]);

  if (!dashboardLayout) return <div>Loading...</div>;

  const DashboardGrid = styled.div`
    display: grid;
    grid-template-columns: ${dashboardLayout.grid};
    gap: 20px;
    padding: 20px;
  `;

  const AIWidget = ({ config, data, styling }) => {
    const StyledWidget = styled.div`
      background: ${styling.background};
      border-radius: ${styling.borderRadius};
      padding: ${styling.padding};
      box-shadow: ${styling.shadow};
      transition: all 0.3s ease;

      &:hover {
        transform: scale(1.02);
        box-shadow: 0 6px 8px rgba(0,0,0,0.15);
      }
    `;

    return (
      <StyledWidget>
        <h2>{config.title}</h2>
        {config.visualization === 'chart' ? (
          <SmartChart data={data} context={config.title} />
        ) : (
          data && JSON.stringify(data)
        )}
      </StyledWidget>
    );
  };

  return (
    <DashboardGrid>
      {dashboardLayout.widgets.map(widget => (
        <AIWidget
          key={widget.id}
          config={widget}
          data={widget.dataSource === 'sensors' ? sensorData : irrigationData}
          styling={widget.aiStyling}
        />
      ))}
    </DashboardGrid>
  );
};

export default AIDashboard;
