import React, { useState } from 'react';
import './chatbot.css';

const Chatbot = () => {
  const [mensagem, setMensagem] = useState('');
  const [conversa, setConversa] = useState([]);
  const [carregando, setCarregando] = useState(false);

  const enviarMensagem = async () => {
    if (!mensagem.trim()) return;

    const novaConversa = [...conversa, { autor: 'usu√°rio', texto: mensagem }];
    setConversa(novaConversa);
    setMensagem('');
    setCarregando(true);

    try {
      const resposta = await fetch('http://localhost:5000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pergunta: mensagem }),
      });

      const data = await resposta.json();
      setConversa([...novaConversa, { autor: 'bot', texto: data.resposta }]);
    } catch (error) {
      setConversa([...novaConversa, { autor: 'bot', texto: '‚ùå Erro ao se comunicar com o AgroBot.' }]);
    } finally {
      setCarregando(false);
    }
  };

  const pressionarEnter = (e) => {
    if (e.key === 'Enter') enviarMensagem();
  };

  return (
    <div className="chat-wrapper">
      <div className="chat-card">
        <h2>ü§ñ <span>AgroBot</span> - Consultoria para Caf√©</h2>
        <div className="chat-box">
          {conversa.map((msg, i) => (
            <div key={i} className={`mensagem ${msg.autor}`}>
              <span>{msg.texto}</span>
            </div>
          ))}
          {carregando && <div className="mensagem bot"><span>‚è≥ Respondendo...</span></div>}
        </div>
        <div className="input-area">
          <input
            type="text"
            placeholder="Digite sua pergunta sobre caf√©..."
            value={mensagem}
            onChange={(e) => setMensagem(e.target.value)}
            onKeyDown={pressionarEnter}
          />
          <button onClick={enviarMensagem}>Enviar</button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
