import React, { useState, useEffect, useRef } from 'react';
import { Send, Leaf, User, Bot, Sparkles, Sun, Droplets } from 'lucide-react';

const Chatbot = () => { // Renamed component
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Ol√°! Sou o AgroAssistente, seu consultor agr√≠cola digital. Como posso ajud√°-lo hoje? üå±",
      type: 'bot',
      timestamp: new Date()
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const simulateBotResponse = (userMessage) => {
    const responses = [
      "Baseado na sua quest√£o sobre cultivo, recomendo verificar o pH do solo primeiro. Um solo equilibrado √© fundamental para o crescimento saud√°vel das plantas.",
      "Para essa situa√ß√£o, sugiro implementar um sistema de irriga√ß√£o por gotejamento. √â mais eficiente e economiza √°gua.",
      "Considerando as condi√ß√µes clim√°ticas atuais, √© importante monitorar a umidade do solo. Posso ajud√°-lo com t√©cnicas de manejo adequadas.",
      "Essa √© uma excelente pergunta sobre agricultura sustent√°vel. Vamos trabalhar juntos para encontrar a melhor solu√ß√£o para sua propriedade.",
      "Com base na an√°lise dos dados, recomendo ajustar o cronograma de plantio. O timing √© crucial para maximizar a produtividade."
    ];
    
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: inputValue,
      type: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsTyping(true);

    // Simular delay de resposta do bot
    setTimeout(() => {
      const botResponse = {
        id: Date.now() + 1,
        text: simulateBotResponse(inputValue),
        type: 'bot',
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, botResponse]);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#e8f5e9] via-[#c8e6c9] to-[#a5d6a7] p-4"> {/* Updated gradient colors */}
      {/* Background decorative elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 opacity-10">
          <Leaf size={120} className="text-[#2e7d32] animate-pulse" /> {/* Using theme primary color */}
        </div>
        <div className="absolute bottom-32 right-16 opacity-10">
          <Sun size={100} className="text-[#FFC107] animate-spin" style={{animationDuration: '20s'}} /> {/* Using theme warning color */}
        </div>
        <div className="absolute top-1/2 left-1/4 opacity-10">
          <Droplets size={80} className="text-[#64B5F6] animate-bounce" /> {/* Using theme accent color */}
        </div>
      </div>

      {/* Main chatbot container */}
      <div className="max-w-4xl mx-auto relative bg-white rounded-3xl shadow-xl overflow-hidden"> {/* Added bg-white, rounded, shadow */}
        {/* Header */}
        <div className="bg-white/90 backdrop-blur-lg rounded-t-3xl border-b border-[#e0e0e0] p-6 shadow-md"> {/* Updated border and shadow */}
          <div className="flex items-center justify-center space-x-3">
            <div className="relative">
              <div className="absolute inset-0 bg-[#4caf50] rounded-full animate-ping opacity-30"></div> {/* Using theme secondary color */}
              <div className="relative bg-gradient-to-r from-[#2e7d32] to-[#4caf50] rounded-full p-3"> {/* Using theme primary/secondary */}
                <Leaf size={28} className="text-white" />
              </div>
            </div>
            <div className="text-center">
              <h1 className="text-3xl font-bold bg-gradient-to-r from-[#2e7d32] to-[#4caf50] bg-clip-text text-transparent"> {/* Using theme primary/secondary */}
                AgroChat
              </h1>
              <p className="text-[#2e7d32]/70 text-sm font-medium"> {/* Using theme primary color */}
                Seu Consultor Agr√≠cola Inteligente
              </p>
            </div>
            <div className="animate-pulse">
              <Sparkles size={24} className="text-[#4caf50]" /> {/* Using theme secondary color */}
            </div>
          </div>
        </div>

        {/* Chat messages area */}
        <div className="chat-messages bg-white/80 backdrop-blur-sm h-96 overflow-y-auto p-6 space-y-4 border-x border-[#e0e0e0]"> {/* Added chat-messages class, updated border */}
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex items-start space-x-3 animate-fadeIn ${
                message.type === 'user' ? 'justify-end' : 'justify-start'
              }`}
            >
              {message.type === 'bot' && (
                <div className="flex-shrink-0">
                  <div className="bg-gradient-to-r from-[#2e7d32] to-[#4caf50] rounded-full p-2"> {/* Using theme primary/secondary */}
                    <Bot size={16} className="text-white" />
                  </div>
                </div>
              )}
              
              <div
                className={`max-w-xs lg:max-w-md px-4 py-3 rounded-2xl shadow-sm transform transition-all duration-300 hover:scale-105 ${
                  message.type === 'user'
                    ? 'bg-gradient-to-r from-[#2e7d32] to-[#4caf50] text-white rounded-br-lg' // Updated rounded-br-sm to rounded-br-lg
                    : 'bg-white border border-[#e0e0e0] text-gray-800 rounded-bl-lg' // Updated rounded-bl-sm to rounded-bl-lg, updated border
                }`}
              >
                <p className="text-sm leading-relaxed">{message.text}</p>
                <p className={`text-xs mt-2 ${
                  message.type === 'user' ? 'text-green-100' : 'text-gray-500'
                }`}>
                  {formatTime(message.timestamp)}
                </p>
              </div>

              {message.type === 'user' && (
                <div className="flex-shrink-0">
                  <div className="bg-gradient-to-r from-[#64B5F6] to-[#3b82f6] rounded-full p-2"> {/* Using theme accent color */}
                    <User size={16} className="text-white" />
                  </div>
                </div>
              )}
            </div>
          ))}

          {/* Typing indicator */}
          {isTyping && (
            <div className="flex items-start space-x-3 animate-pulse">
              <div className="bg-gradient-to-r from-[#2e7d32] to-[#4caf50] rounded-full p-2"> {/* Using theme primary/secondary */}
                <Bot size={16} className="text-white" />
              </div>
              <div className="bg-white border border-[#e0e0e0] rounded-2xl rounded-bl-lg px-4 py-3 shadow-sm"> {/* Updated border and rounded-bl-sm to rounded-bl-lg */}
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-[#4caf50] rounded-full animate-bounce"></div> {/* Using theme secondary color */}
                  <div className="w-2 h-2 bg-[#4caf50] rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div> {/* Using theme secondary color */}
                  <div className="w-2 h-2 bg-[#4caf50] rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div> {/* Using theme secondary color */}
                </div>
              </div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        {/* Input area */}
        <div className="bg-white/90 backdrop-blur-lg rounded-b-3xl border-t border-[#e0e0e0] p-6 shadow-md"> {/* Updated border and shadow */}
          <div className="flex space-x-4">
            <div className="flex-1 relative">
              <textarea
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Digite sua pergunta sobre agricultura..."
                className="w-full px-4 py-3 bg-white border border-[#e0e0e0] rounded-2xl focus:outline-none focus:ring-2 focus:ring-[#4caf50] focus:border-transparent resize-none transition-all duration-300 text-gray-800 placeholder-gray-500" // Updated border and focus ring
                rows="2"
                disabled={isTyping}
              />
              <div className="absolute bottom-2 right-2 text-xs text-gray-400">
                Shift + Enter para nova linha
              </div>
            </div>
            
            <button
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isTyping}
              className="bg-gradient-to-r from-[#2e7d32] to-[#4caf50] hover:from-[#4caf50] hover:to-[#2e7d32] disabled:from-gray-300 disabled:to-gray-400 text-white p-3 rounded-2xl transition-all duration-300 transform hover:scale-105 disabled:scale-100 disabled:cursor-not-allowed shadow-lg hover:shadow-xl" // Updated gradient colors
            >
              <Send size={20} />
            </button>
          </div>

          {/* Quick action buttons */}
          <div className="flex flex-wrap gap-2 mt-4">
            {[
              "Como melhorar o solo?",
              "Dicas de irriga√ß√£o",
              "Controle de pragas",
              "Melhor √©poca para plantar"
            ].map((suggestion, index) => (
              <button
                key={index}
                onClick={() => setInputValue(suggestion)}
                className="px-3 py-1 bg-[#e8f5e9] hover:bg-[#c8e6c9] text-[#2e7d32] text-xs rounded-full transition-colors duration-200 border border-[#a5d6a7] hover:border-[#4caf50]" // Updated colors
              >
                {suggestion}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Removed inline style block */}
    </div>
  );
};

export default Chatbot; // Renamed export
