import React, { useState, useEffect, useRef } from 'react';
import { Send, Leaf, User, Bot, Sparkles, Sun, Droplets } from 'lucide-react';

const Chatbot = () => { // Renamed component
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Olá! Sou o AgroAssistente, seu consultor agrícola digital. Como posso ajudá-lo hoje? 🌱",
      type: 'bot',
      timestamp: new Date()
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const simulateBotResponse = (userMessage) => {
    const responses = [
      "Baseado na sua questão sobre cultivo, recomendo verificar o pH do solo primeiro. Um solo equilibrado é fundamental para o crescimento saudável das plantas.",
      "Para essa situação, sugiro implementar um sistema de irrigação por gotejamento. É mais eficiente e economiza água.",
      "Considerando as condições climáticas atuais, é importante monitorar a umidade do solo. Posso ajudá-lo com técnicas de manejo adequadas.",
      "Essa é uma excelente pergunta sobre agricultura sustentável. Vamos trabalhar juntos para encontrar a melhor solução para sua propriedade.",
      "Com base na análise dos dados, recomendo ajustar o cronograma de plantio. O timing é crucial para maximizar a produtividade."
    ];
    
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: inputValue,
      type: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsTyping(true);

    // Simular delay de resposta do bot
    setTimeout(() => {
      const botResponse = {
        id: Date.now() + 1,
        text: simulateBotResponse(inputValue),
        type: 'bot',
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, botResponse]);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="h-full w-full flex flex-col items-center justify-center p-4"> {/* Added back p-4 for overall padding */}
      {/* Main chatbot container */}
      <div className="max-w-4xl w-full relative bg-white rounded-xl shadow-lg overflow-hidden flex flex-col h-full border border-gray-200"> {/* Adjusted rounded, shadow, added border */}
        {/* Background decorative elements - now more subtle and within the container */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none z-0">
          <div className="absolute top-1/4 left-1/4 opacity-5"> {/* Reduced opacity */}
            <Leaf size={100} className="text-[#2e7d32]" /> {/* Smaller size, no animation */}
          </div>
          <div className="absolute bottom-1/4 right-1/4 opacity-5"> {/* Reduced opacity */}
            <Sun size={80} className="text-[#FFC107]" /> {/* Smaller size, no animation */}
          </div>
          <div className="absolute top-1/2 left-1/2 opacity-5 transform -translate-x-1/2 -translate-y-1/2"> {/* Reduced opacity */}
            <Droplets size={60} className="text-[#64B5F6]" /> {/* Smaller size, no animation */}
          </div>
        </div>

        {/* Header */}
        <div className="bg-gray-50 border-b border-gray-200 p-4 flex items-center justify-between z-10"> {/* Simpler header */}
          <div className="flex items-center space-x-3">
            <div className="bg-[#2e7d32] rounded-full p-2"> {/* Agro-themed icon background */}
              <Leaf size={20} className="text-white" />
            </div>
            <h1 className="text-xl font-semibold text-gray-800">AgroChat</h1>
          </div>
          <p className="text-sm text-gray-500">Seu Consultor Agrícola Inteligente</p>
        </div>

        {/* Chat messages area */}
        <div className="chat-messages flex-grow overflow-y-auto p-4 space-y-4 bg-gray-50 z-10"> {/* Simpler background */}
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex items-start space-x-3 ${
                message.type === 'user' ? 'justify-end' : 'justify-start'
              }`}
            >
              {message.type === 'bot' && (
                <div className="flex-shrink-0 bg-gray-200 rounded-full p-2"> {/* Neutral bot avatar background */}
                  <Bot size={16} className="text-gray-700" />
                </div>
              )}
              
              <div
                className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg shadow-sm ${ /* Simpler rounded, no hover scale */
                  message.type === 'user'
                    ? 'bg-[#2e7d32] text-white rounded-br-none' /* Agro primary for user */
                    : 'bg-gray-100 text-gray-800 rounded-bl-none border border-gray-200' /* Light gray for bot */
                }`}
              >
                <p className="text-sm leading-relaxed">{message.text}</p>
                <p className={`text-xs mt-1 ${
                  message.type === 'user' ? 'text-green-100' : 'text-gray-500'
                }`}>
                  {formatTime(message.timestamp)}
                </p>
              </div>

              {message.type === 'user' && (
                <div className="flex-shrink-0 bg-blue-200 rounded-full p-2"> {/* Neutral user avatar background */}
                  <User size={16} className="text-blue-700" />
                </div>
              )}
            </div>
          ))}

          {/* Typing indicator */}
          {isTyping && (
            <div className="flex items-start space-x-3 animate-pulse">
              <div className="flex-shrink-0 bg-gray-200 rounded-full p-2">
                <Bot size={16} className="text-gray-700" />
              </div>
              <div className="bg-gray-100 border border-gray-200 rounded-lg rounded-bl-none px-4 py-2 shadow-sm">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                </div>
              </div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        {/* Input area */}
        <div className="bg-white border-t border-gray-200 p-4 shadow-md z-10"> {/* Simpler input area background */}
          <div className="flex space-x-3 items-end"> {/* Adjusted alignment */}
            <div className="flex-1 relative">
              <textarea
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Envie uma mensagem..." /* Simpler placeholder */
                className="w-full px-4 py-2 bg-gray-50 border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-[#2e7d32] focus:border-transparent resize-none transition-all duration-300 text-gray-800 placeholder-gray-500" /* Simpler styling */
                rows="3" /* Adjusted rows */
                disabled={isTyping}
              />
              <div className="absolute bottom-2 right-2 text-xs text-gray-400">
                Shift + Enter para nova linha
              </div>
            </div>
            
            <button
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isTyping}
              className="bg-[#2e7d32] hover:bg-[#4caf50] disabled:bg-gray-300 text-white p-3 rounded-lg transition-colors duration-200 shadow-md disabled:cursor-not-allowed" /* Simpler button styling */
            >
              <Send size={20} />
            </button>
          </div>

          {/* Quick action buttons */}
          <div className="flex flex-wrap gap-2 mt-3 justify-center"> {/* Centered suggestions */}
            {[
              "Como melhorar o solo?",
              "Dicas de irrigação",
              "Controle de pragas",
              "Melhor época para plantar"
            ].map((suggestion, index) => (
              <button
                key={index}
                onClick={() => setInputValue(suggestion)}
                className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-700 text-xs rounded-full transition-colors duration-200 border border-gray-200" /* Simpler suggestion button styling */
              >
                {suggestion}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chatbot; // Renamed export
