import React, { useState, useEffect } from 'react';
import { ResponsiveContainer, LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid } from 'recharts'; // Added CartesianGrid
import aiTheme from '../styles/theme'; // Import theme

const aiModel = {
  designChart: async ({ data, context, style, interactivity, accessibility }) => {
    // Mock AI chart design
    console.log('AI designing chart');
    return {
      containerStyle: {
        background: aiTheme.colors.white,
        borderRadius: aiTheme.borderRadius,
        padding: aiTheme.spacing.medium,
        boxShadow: aiTheme.boxShadow,
        border: `1px solid ${aiTheme.colors.border}`, // Added border
      },
      xAxis: {
        key: 'time',
        color: aiTheme.colors.lightText,
        style: {
          fontSize: '12px',
          fill: aiTheme.colors.lightText, // Text color for axis labels
        },
      },
      yAxis: {
        color: aiTheme.colors.lightText,
        style: {
          fontSize: '12px',
          fill: aiTheme.colors.lightText, // Text color for axis labels
        },
      },
      line: {
        type: 'monotone',
        dataKey: 'value',
        color: aiTheme.colors.primary, // Use theme primary color for line
        width: 3, // Slightly thicker line
        dot: false,
      },
      tooltip: {
        background: aiTheme.colors.white,
        borderRadius: aiTheme.borderRadius,
        padding: aiTheme.spacing.small,
        boxShadow: aiTheme.boxShadow,
        border: `1px solid ${aiTheme.colors.border}`, // Added border
        color: aiTheme.colors.text, // Text color for tooltip
      },
      grid: { // Added grid styling
        strokeDasharray: '3 3',
        stroke: aiTheme.colors.border,
      },
      height: 300,
    };
  },
};

const SmartChart = ({ data, context }) => {
  const [chartConfig, setChartConfig] = useState(null);

  useEffect(() => {
    const generateChart = async () => {
      const config = await aiModel.designChart({
        data: data,
        context: context,
        style: 'agricultural_professional',
        interactivity: 'high',
        accessibility: true,
      });

      setChartConfig(config);
    };

    generateChart();
  }, [data, context]);

  if (!chartConfig) return <div>Loading...</div>;

  const CustomTooltip = ({ aiStyle, payload, label }) => {
    if (!payload || payload.length === 0) return null;

    return (
      <div style={aiStyle}>
        <p className="label">{`${label} : ${payload[0].value}`}</p>
      </div>
    );
  };

  return (
    <ResponsiveContainer width="100%" height={chartConfig.height}>
      <LineChart data={data} style={chartConfig.containerStyle}>
        <CartesianGrid strokeDasharray={chartConfig.grid.strokeDasharray} stroke={chartConfig.grid.stroke} /> {/* Added CartesianGrid */}
        <XAxis
          dataKey={chartConfig.xAxis.key}
          stroke={chartConfig.xAxis.color}
          style={chartConfig.xAxis.style}
        />
        <YAxis
          stroke={chartConfig.yAxis.color}
          style={chartConfig.yAxis.style}
        />
        <Tooltip
          content={<CustomTooltip aiStyle={chartConfig.tooltip} />}
        />
        <Line
          type={chartConfig.line.type}
          dataKey={chartConfig.line.dataKey}
          stroke={chartConfig.line.color}
          strokeWidth={chartConfig.line.width}
          dot={chartConfig.line.dot}
        />
      </LineChart>
    </ResponsiveContainer>
  );
};

export default SmartChart;
