import React, { useState } from 'react';
import { Leaf, MessageCircle, AreaChart, TrendingUp, BarChart3 } from 'lucide-react';
import PlotCard from './PlotCard';
import AgroBot from './AgroBot';
import './Chatbot.css';

const Chatbot = () => {
  const [activeTab, setActiveTab] = useState('chat');
  const [suggestions, setSuggestions] = useState([]);

  const mockPlots = [
    { id: 1, name: 'Lote 1 - Milho', status: 'Crescimento', health: 'Excelente', area: '2.5 ha', imageUrl: 'https://via.placeholder.com/150/a8e6cf/000000?text=Milho' },
    { id: 2, name: 'Lote 2 - Soja', status: 'Plantio', health: 'Bom', area: '3.0 ha', imageUrl: 'https://via.placeholder.com/150/d4f8e8/000000?text=Soja' },
    { id: 3, name: 'Lote 3 - Trigo', status: 'Colheita', health: 'Ótimo', area: '2.0 ha', imageUrl: 'https://via.placeholder.com/150/b2f0d4/000000?text=Trigo' },
  ];

  const mockChartData = [
    { time: 'Jan', value: 65 },
    { time: 'Fev', value: 59 },
    { time: 'Mar', value: 80 },
    { time: 'Abr', value: 81 },
    { time: 'Mai', value: 56 },
    { time: 'Jun', value: 70 },
  ];

  const SimpleChart = ({ data }) => (
    <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-200">
      <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
        <BarChart3 size={20} className="text-green-600" />
        Produtividade Mensal
      </h3>
      <div className="space-y-3">
        {data.map((item, index) => (
          <div key={index} className="flex items-center justify-between">
            <span className="text-sm font-medium text-gray-600">{item.time}</span>
            <div className="flex items-center gap-3">
              <div className="w-32 bg-gray-200 rounded-full h-2">
                <div
                  className="bg-gradient-to-r from-green-500 to-emerald-600 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${(item.value / 100) * 100}%` }}
                ></div>
              </div>
              <span className="text-sm font-semibold text-green-600">{item.value}%</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-emerald-50 to-teal-50 p-4 flex items-center justify-center">
      {/* Floating background elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-10 left-10 opacity-10 animate-pulse">
          <Leaf size={120} className="text-green-600" />
        </div>
        <div className="absolute bottom-20 right-10 opacity-10 animate-pulse" style={{ animationDelay: '1s' }}>
          <Sun size={100} className="text-yellow-500" />
        </div>
        <div className="absolute top-1/2 right-20 opacity-10 animate-pulse" style={{ animationDelay: '2s' }}>
          <Droplets size={80} className="text-blue-500" />
        </div>
      </div>

      {/* Main container */}
      <div className="w-full max-w-7xl h-[90vh] flex flex-col bg-white/80 backdrop-blur-lg rounded-3xl shadow-2xl border border-white/20 overflow-hidden">

        {/* Enhanced Header with Tabs */}
        <div className="bg-gradient-to-r from-green-600 to-emerald-600 p-6 text-white relative overflow-hidden">
          <div className="absolute inset-0 bg-black/10"></div>
          <div className="relative z-10">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-4">
                <div className="bg-white/20 backdrop-blur-sm rounded-full p-3 ring-2 ring-white/30">
                  <Leaf size={24} className="text-white" />
                </div>
                <div>
                  <h1 className="text-2xl font-bold">AgroChat Pro</h1>
                  <p className="text-green-100 text-sm">Sua Plataforma Agrícola Inteligente</p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse"></div>
                <span className="text-sm text-green-100">Sistema Online</span>
              </div>
            </div>

            {/* Navigation Tabs */}
            <div className="flex space-x-1 bg-white/10 rounded-lg p-1">
              {[
                { id: 'chat', label: 'Chat IA', icon: MessageCircle },
                { id: 'plots', label: 'Meus Lotes', icon: AreaChart },
                { id: 'analytics', label: 'Análises', icon: TrendingUp }
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-md transition-all duration-300 ${activeTab === tab.id
                    ? 'bg-white text-green-600 shadow-md'
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                    }`}
                  aria-controls={`${tab.id}-panel`}
                  role="tab"
                  aria-selected={activeTab === tab.id}
                  tabIndex={activeTab === tab.id ? 0 : -1}
                >
                  <tab.icon size={16} />
                  {tab.label}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Content Area */}
        <div className="flex-1 overflow-hidden">

          {/* Chat Tab */}
          {activeTab === 'chat' && (
            <div className="h-full flex flex-col" id="chat-panel" role="tabpanel" aria-labelledby="chat">
              <AgroBot />
            </div>
          )}

          {/* Plots Tab */}
          {activeTab === 'plots' && (
            <div className="h-full overflow-y-auto p-6 bg-gradient-to-b from-green-50/50 to-white/50 custom-scrollbar" id="plots-panel" role="tabpanel" aria-labelledby="plots">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-gray-800 mb-2">Gestão de Lotes</h2>
                <p className="text-gray-600">Monitore e gerencie seus lotes agrícolas</p>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {mockPlots.map((plot) => (
                  <PlotCard key={plot.id} plot={plot} />
                ))}
              </div>

              {/* Add new plot button */}
              <div className="mt-8 flex justify-center">
                <button className="bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white px-6 py-3 rounded-xl shadow-lg transition-all duration-300 hover:scale-105 focus-ring">
                  + Adicionar Novo Lote
                </button>
              </div>
            </div>
          )}

          {/* Analytics Tab */}
          {activeTab === 'analytics' && (
            <div className="h-full overflow-y-auto p-6 bg-gradient-to-b from-green-50/50 to-white/50 custom-scrollbar" id="analytics-panel" role="tabpanel" aria-labelledby="analytics">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-gray-800 mb-2">Análises e Relatórios</h2>
                <p className="text-gray-600">Dados inteligentes para decisões assertivas</p>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <SimpleChart data={mockChartData} />

                <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-200">
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Métricas Gerais</h3>
                  <div className="space-y-4">
                    <div className="flex justify-between items-center p-3 bg-green-50 rounded-lg">
                      <span className="text-green-800 font-medium">Total de Hectares</span>
                      <span className="text-green-600 font-bold">7.5 ha</span>
                    </div>
                    <div className="flex justify-between items-center p-3 bg-blue-50 rounded-lg">
                      <span className="text-blue-800 font-medium">Lotes Irrigados</span>
                      <span className="text-blue-600 font-bold">2/3</span>
                    </div>
                    <div className="flex justify-between items-center p-3 bg-yellow-50 rounded-lg">
                      <span className="text-yellow-800 font-medium">Produtividade Média</span>
                      <span className="text-yellow-600 font-bold">84%</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
